    long bitmask = 1L << (position.getSquareIndex());
    long bitFilter = ~bitmask;
    for (int i = 0; i < 4; i++) {
        if ((pieceCodeColor & 1) != 0) {
            _boardLayer[i] |= bitmask;// Set this bit to 1.

        } else {
