            Piece p = board.getPiece(curPosition);
            if (p != null) {
                val ^= _factors[p.getColor()][((p.getType()) - 1)][square];
            }
        }
        emptySquareMask >>>= 1;// Shift the mask to test for the next square.

    }
    if (!white) {
        val ^= _blackMoves;
    }
    return val;
}

